import "./home.css";
import { useState, useEffect } from "react";
import api from "../../service/api";
import { Link } from "react-router-dom";

//api_key=41b87a10fbb8701f5f314796b61a1eeb&language=pt-BR

function Home() {
  const [Filmes, setFilmes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [busca, setBusca] = use

  useEffect(() => {
    //função para fazer a requisição da api
    async function loadMovies() {
      await api
        .get("movie/now_playing", {
          params: {
            api_key: "41b87a10fbb8701f5f314796b61a1eeb",
            language: "pt-BR",
            page: 1,
          },
        })
        .then((response) => {
          console.log("Funcionou");
          setFilmes(response.data.results.slice(0, 10));
          setLoading(false);
        })
        .catch(() => {
          console.error("Filmes não encontrado");
          return;
        });
    }
    loadMovies();
  }, []);

  if (loading) {
    return (
      <div className="loading">
        <h2>Carregando filmes ...</h2>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="list-filmes">
        {Filmes.map((filme) => {
          return (
            <article key={filme.id}>
              <strong>{filme.title}</strong>
              <img
                src={`https://image.tmdb.org/t/p/original/${filme.poster_path}`}
                alt={filme.title}
              ></img>
              <Link to={`/filme/${filme.id}`}>Acessar</Link>
            </article>
          );
        })}
      </div>
    </div>
  );
}

export default Home;
