import "./home.css";
import { useState, useEffect } from "react";
import api from "../../service/api";
import { Link } from "react-router-dom";


//api_key=41b87a10fbb8701f5f314796b61a1eeb&language=pt-BR

function Home() {
  const [filmes, setFilmes] = useState([]);
  const [loading, setLoading] = useState(true);
   const [busca, setBusca] = useState("");
   const [category, setCategory] = useState("movie");

  const tolowercaseBusca = busca.toLowerCase();
  const filmesFiltro = filmes.filter((f) =>
    (f.title || f.name).toLowerCase().startsWith(tolowercaseBusca)
  );

  useEffect(() => {
    console.log(`Requisitando: /${category}`);
    //função para fazer a requisição da api
    async function loadMovies() {
      await api
        .get(`${category}/week`, {
          params: {
            api_key: "41b87a10fbb8701f5f314796b61a1eeb",
            language: "pt-BR",
            page: 1,
          },
        })
        .then((response) => {
          setFilmes(response.data.results.slice(0, 12));
          setLoading(false);
        })
        .catch(() => {
          console.error("Filmes não encontrado");
          return;
        });
    }
    loadMovies();
  }, [category]);

  if (loading) {
    return (
      <div className="loading">
        <h2>Carregando filmes ...</h2>
      </div>
    );
  }

  return (
    <div className="container">
       <div className="search-box">
        <label htmlFor="search-form">
        <button class="btn-search"><i class="fas fa-search"></i></button>
          <input
            type="search"
            name="search-form"
            id="search-form"
            className="input-search"
            placeholder="Qual filme você procura?"
            value={busca}
            onChange={(e) => setBusca(e.target.value)}
          ></input>
          {/* <span className="sr-only"> Search movies here</span> */}
        </label>
        </div>

        <div className="custom-select">
          <select
            value={ category }
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value={"movie"}>Filmes</option>
            <option value={"tv"}>Series</option>
          </select>
        </div>

      <div className="list-filmes">
        {filmesFiltro.map((filme) => {
          return (
            <article key={filme.id}>
              <strong>{filme.title || filme.name}</strong>
              <img
                src={`https://image.tmdb.org/t/p/original/${filme.poster_path}`}
                alt={filme.title || filme.name}
              />
              <Link to={`/${category}/${filme.id}`}>Acessar</Link>
            </article>
          );
        })}
      </div>
    </div>
  );
}

export default Home;
