import "./filme.css";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import apiID from "../../service/apiID";
function Filme() {
  const { type, id } = useParams();
  const [filme, setFilme] = useState({});
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function loadFilme() {
      console.log(`Requisitando: /${type}/${id}`);
      await apiID
        .get(`/${type}/${id}`, {
          params: {
            api_key: "41b87a10fbb8701f5f314796b61a1eeb",
            language: "pt-BR",
          },
        })
        .then((response) => {
          setFilme(response.data);
          setLoading(false);
        })
        .catch(() => {
          console.error("Filme não encontrado!");
          navigate("/", { replace: true });
          return;
        });
    }
    loadFilme();
    return () => {
      console.log("Componente desmontado");
    };
  }, [navigate,type,id]);

  function favoritar(){
    const minhaLista = localStorage.getItem("@primeflix")
    let favoritos = JSON.parse(minhaLista) || []

    const hasFilme = favoritos.some((filmesSalvo) => filmesSalvo.id === filme.id)
    if(hasFilme){
        alert("Filme ja foi salvo!")
        return
    }
    favoritos.push(filme)
    localStorage.setItem(favoritos)
  }

  if(loading){
    return(
        <div className="loading">
            <h2>Carregando...</h2>
        </div>
    )
  }

  return (
    <div className="filme-info">
        <h1>{filme.title}</h1>

        <img
        src={`https://image.tmdb.org/t/p/original/${filme.backdrop_path}`}
        alt={filme.title}
        ></img>
        <h3>Sinopse</h3>

        <span>{filme.overiew}</span>
        <strong>Avaliação: {filme.vote_avverage} / 10</strong>

        <div className="area-buttons">
            <button onClick={favoritar}>Salvar</button>
            <button>
                <a target="blank" href={`https://youtube.com/results?search_query=${filme.title}`}>Trailer</a>
            </button>
        </div>
    </div>
  );
}

export default Filme;
